definition.script:
node {
    paramDEPLOYBRANCH = "UAT"

    stage('Level 1 - Provision server') {
        build job: './1.1-build-pipeline-jobs/level-1-build-server', parameters: [
            string(name: 'TEAM_AREA', value: paramTEAM_AREA)
        ]
    }

    stage('Level 2 - Install Vanilla Application') {
        build job: './1.1-build-pipeline-jobs/level-2-install-vanilla-application', parameters: [
            string(name: 'TARGETSERVERIP', value: $BUILD_PRIVATE_IP)
        ]
    }

    stage('Level 3 - Deploy A Specific version of the codes') {
        build job: './1.1-build-pipeline-jobs/level-3-deploy-a-specific-version-of-the-codes', parameters: [
           string(name: 'TARGETSERVERIP', value: $BUILD_PRIVATE_IP),
           string(name: 'DEPLOYBRANCH', value: paramDEPLOYBRANCH)
        ]
    }

    stage('Final - Build Pipeline Completed') {
        echo 'Hello World'
    }
}
